mapper.insert:
INSERT into auction (
board_idx,
auction_highPrice
)
values (
#{board_ref},
#{action_highPrice}
)

mapper.update:
UPDATE auction 
set 
auction_highPrice = (
SELECT MAX(auctionOrder_used)
		from auctionOrder 
		WHERE auction_idx = #{auction_idx}
)
WHERE 
auction_idx = #{auction_idx}

mapper.insert:
INSERT into auctionOrder (
auction_idx,
auctionOrder_used,
user_idx
)
values (
#{auction_idx},
#{auctionOrder_used}
#{user_idx}
)
