mapper.insert:
INSERT into auction (
board_idx,
auction_highPrice
)
values (
#{board_ref},
#{action_highPrice}
)

mapper.update:
UPDATE auction 
set 
auction_highPrice = (
SELECT MAX(auctionOrder_used)
		from auctionOrder 
		WHERE auction_idx = #{auction_idx}
)
WHERE 
auction_idx = #{auction_idx}

UPDATE auction 
set 
auctionCol1 = #{auctionCol1}
WHERE
auction_idx = #{auction_idx}

UPDATE auction 
set 
auction_highPrice = (
SELECT
	board_price
	from board
	WHERE board_idx = #{board_idx}
)
WHERE 
board_idx = #{board_idx}

mapper.insert:
INSERT into auctionOrder (
auction_idx,
auctionOrder_used,
user_idx
)
values (
#{auction_idx},
#{auctionOrder_used}
#{user_idx}
)

mapper.update:
UPDATE auctionOrder 
set 
auctionOrder_used = #{auctionOrder_used}
WHERE 
auction_idx = #{auction_idx}
AND
user_idx = #{user_idx}
